import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "../../components/header/Header";
import Pagination from "../../components/pagination/Pagination";
import "./home.css";

export default function Home() {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [recordsPerPage] = useState(12);

  useEffect(() => {
    getEmployees();
  }, []);

  const getEmployees = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/employees");
      setEmployees(res.data);
      setLoading(false);
    } catch (error) {
      console.error("‚ùå Error fetching employees:", error);
      setLoading(false);
    }
  };

  // ‚úÖ Pagination Logic
  const indexOfLastRecord = currentPage * recordsPerPage;
  const indexOfFirstRecord = indexOfLastRecord - recordsPerPage;
  const currentRecords = employees.slice(indexOfFirstRecord, indexOfLastRecord);
  const nPages = Math.ceil(employees.length / recordsPerPage);

  // ‚úÖ Format Date and Time to 12-hour format
  const formatDateTime = (dateTime) => {
    if (!dateTime) return "N/A";
    const date = new Date(dateTime);
    const options = { hour: 'numeric', minute: 'numeric', hour12: true };
    return `${date.toLocaleDateString()} ${date.toLocaleTimeString([], options)}`;
  };

  return (
    <section className="main">
      {loading && <div className="loading-screen">‚è≥ Loading Data...</div>}

      <Header />

      {/* ‚úÖ Employee Table */}
      <div className="table-container">
        <div className="table-header">
          <h2>üìã Employee Records</h2>
          <div className="table-buttons">
            <button onClick={() => window.location.href = "/employee/add"} className="top-btn">‚ûï Add Employee</button>
            <button onClick={() => window.location.href = "/employee/search"} className="top-btn">üîç Search</button>
          </div>
        </div>
        <table className="employee-table">
          <thead>
            <tr>
              <th>Employee ID</th>
              <th>Timestamp</th>
            </tr>
          </thead>
          <tbody>
            {currentRecords.map((emp) => (
              <tr key={emp._id}>
                <td>{emp.employeeId}</td>
                <td>{formatDateTime(emp.timestamp)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <Pagination nPages={nPages} currentPage={currentPage} setCurrentPage={setCurrentPage} />
    </section>
  );
}
