import React, { useState, useEffect } from "react";
import axios from "axios";
import { PieChart, Pie, Cell, Tooltip, Legend, LineChart, Line, XAxis, YAxis, CartesianGrid } from "recharts";
import Header from "../../components/header/Header";
import Pagination from "../../components/pagination/Pagination";
import "./home.css";

export default function Home() {
  const [employees, setEmployees] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [recordsPerPage] = useState(12);
  const [employeeId, setEmployeeId] = useState("");

  useEffect(() => {
    getEmployees();
    getAttendance();
  }, []);

  const getEmployees = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/employees");
      setEmployees(res.data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching employees:", error);
      setLoading(false);
    }
  };

  const getAttendance = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/attendance");
      setAttendance(res.data);
    } catch (error) {
      console.error("Error fetching attendance:", error);
    }
  };

  // Handle RFID Attendance
  const handleRfidAttendance = async () => {
    if (!rfidBadgeNumber.trim()) {
      alert("Please enter a valid RFID Number!");
      return;
    }

    setLoading(true);

    try {
      const res = await axios.post("http://localhost:5000/api/attendance/record", { rfidBadgeNumber });
      alert("Attendance recorded successfully");
      getAttendance();
      setRfidBadgeNumber("");
    } catch (error) {
      console.error("Error recording attendance:", error);
      alert(error.response?.data?.message || "An unexpected error occurred.");
    } finally {
      setLoading(false);
    }
  };

  // Pagination Logic
  const indexOfLastRecord = currentPage * recordsPerPage;
  const indexOfFirstRecord = indexOfLastRecord - recordsPerPage;
  const currentRecords = attendance.slice(indexOfFirstRecord, indexOfLastRecord);
  const nPages = Math.ceil(attendance.length / recordsPerPage);

  // Format Date and Time
  const formatDateTime = (dateTime) => {
    if (!dateTime) return "N/A";
    const date = new Date(dateTime);
    return date.toLocaleString();
  };

  return (
    <section className="main">
      {loading && <div className="loading-screen">Loading Data...</div>}

      <Header />

      {/* RFID Scanner Section */}
      <div className="rfid-container">
        <h3>RFID Scanner</h3>
        <div className="rfid-input-group">
          <input
            type="text"
            value={rfidBadgeNumber}
            onChange={(e) => setRfidBadgeNumber(e.target.value)}
            placeholder="Enter RFID Number"
            className="rfid-input"
          />
          <button className="rfid-btn" onClick={handleRfidAttendance}>
            Record Attendance
          </button>
        </div>
      </div>

      {/* Attendance Table */}
      <div className="table-container">
        <div className="table-header">
          <h2>Attendance Records</h2>
          <div className="table-buttons">
            <button onClick={() => window.location.href = "/employee/add"} className="top-btn">Add Employee</button>
            <button onClick={() => window.location.href = "/employee/search"} className="top-btn">Search Employee</button>
          </div>
        </div>
        <table className="employee-table">
          <thead>
            <tr>
              <th>Employee ID</th>
              <th>Name</th>
              <th>Department</th>
              <th>Timestamp</th>
            </tr>
          </thead>
          <tbody>
            {currentRecords.map((record) => (
              <tr key={record._id}>
                <td>{record.employeeId}</td>
                <td>{record.employeeName}</td>
                <td>{record.department}</td>
                <td>{formatDateTime(record.timestamp)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <Pagination nPages={nPages} currentPage={currentPage} setCurrentPage={setCurrentPage} />
    </section>
  );
}
