import React, { useState } from "react";
import "./pagination.css";

export default function Pagination({ 
  nPages, 
  currentPage, 
  setCurrentPage,
  itemsPerPage = 100 
}) {
  const [pageInput, setPageInput] = useState(currentPage);

  // Calculate visible page numbers with truncation
  const getVisiblePages = () => {
    const visiblePages = [];
    const maxVisiblePages = 5; // Adjust this number to show more/less pages
    let startPage = Math.max(1, currentPage - Math.floor(maxVisiblePages / 2));
    let endPage = startPage + maxVisiblePages - 1;

    if (endPage > nPages) {
      endPage = nPages;
      startPage = Math.max(1, endPage - maxVisiblePages + 1);
    }

    // Always show first page
    if (startPage > 1) {
      visiblePages.push(1);
      if (startPage > 2) visiblePages.push('...');
    }

    // Add middle pages
    for (let i = startPage; i <= endPage; i++) {
      visiblePages.push(i);
    }

    // Always show last page
    if (endPage < nPages) {
      if (endPage < nPages - 1) visiblePages.push('...');
      visiblePages.push(nPages);
    }

    return visiblePages;
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= nPages) {
      setCurrentPage(newPage);
    }
  };

  const handlePageInput = (e) => {
    e.preventDefault();
    const page = parseInt(pageInput);
    if (!isNaN(page) && page >= 1 && page <= nPages) {
      setCurrentPage(page);
    }
  };

  return (
    <div className="pagination-container">
      <button
        className="pagination-arrow"
        disabled={currentPage === 1}
        onClick={() => handlePageChange(currentPage - 1)}
        aria-label="Previous page"
      >
        ⟨
      </button>

      {getVisiblePages().map((pgNumber, index) => (
        <button
          key={index}
          className={`pagination-number ${currentPage === pgNumber ? "active" : ""}`}
          disabled={pgNumber === '...'}
          onClick={() => typeof pgNumber === 'number' && handlePageChange(pgNumber)}
        >
          {pgNumber}
        </button>
      ))}

      <button
        className="pagination-arrow"
        disabled={currentPage === nPages}
        onClick={() => handlePageChange(currentPage + 1)}
        aria-label="Next page"
      >
        ⟩
      </button>

      <div className="page-jump">
        <form onSubmit={handlePageInput}>
          <input
            type="number"
            min="1"
            max={nPages}
            value={pageInput}
            onChange={(e) => setPageInput(e.target.value)}
            aria-label="Page number"
          />
          <button type="submit">Go</button>
        </form>
      </div>

      <div className="page-info">
        Page {currentPage} of {nPages} ({itemsPerPage} items per page)
      </div>
    </div>
  );
}
