import React, { useEffect } from "react";
import "./message.css";

export default function Message({ msg, type, removeMessage }) {
  useEffect(() => {
    const timeout = setTimeout(() => {
      removeMessage();
    }, 2000);

    return () => clearTimeout(timeout);
  }, [removeMessage]); // ✅ Include removeMessage in dependencies

  if (!msg) return null; // ✅ Prevent rendering empty messages

  return <p className={`message message-${type}`}>{msg}</p>;
}
