import axios from "axios";
import React, { useState, useCallback, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../../components/header/Header";
import Message from "../../components/message/Message";
import "./add.css";

export default function Add() {
  const navigate = useNavigate();

  const [employee, setEmployee] = useState({
    employeeId: "",
    firstName: "",
    lastName: "",
    department: "",
    rfidBadgeNumber: "",
  });

  const [file, setFile] = useState(null);
  const [previewURL, setPreviewURL] = useState(null);
  const [message, setMessage] = useState({ show: false, msg: "", type: "" });
  const [employeeIdError, setEmployeeIdError] = useState(""); 
  const [rfidError, setRfidError] = useState(""); // Store RFID Badge Number validation error
  const [existingEmployeeError, setExistingEmployeeError] = useState(""); 

  // ✅ Function to show messages with auto-dismiss
  const showMessage = useCallback((show = false, type = "", msg = "") => {
    setMessage({ show, type, msg });
    if (show) {
      setTimeout(() => setMessage({ show: false, msg: "", type: "" }), 3000);
    }
  }, []);

  // ✅ Handle input field changes
  const updateEmployee = (e) => {
    const { name, value } = e.target;

    // Employee ID Validation (Only Numbers & 4 Digits)
    if (name === "employeeId") {
      if (!/^\d{0,4}$/.test(value)) {
        setEmployeeIdError("Employee ID must be exactly 4 numeric digits.");
      } else {
        setEmployeeIdError("");
        checkIfEmployeeExists(value);
      }
    }

    // RFID Badge Number Validation (4 Alphanumeric Characters, No Whitespaces)
    if (name === "rfidBadgeNumber") {
      const rfidPattern = /^[A-Za-z0-9]{4}$/;
      if (!rfidPattern.test(value)) {
        setRfidError("RFID Badge Number must be exactly 4 alphanumeric characters, no spaces.");
      } else {
        setRfidError("");
        checkIfRfidExists(value);
      }
    }

    setEmployee((prev) => ({ ...prev, [name]: value }));
  };

  // ✅ Check if Employee ID Already Exists
  const checkIfEmployeeExists = async (employeeId) => {
    try {
      if (employeeId.length === 4) {
        const res = await axios.get(`http://localhost:5000/api/employees/check-id/${employeeId}`);
        if (res.data.exists) {
          setExistingEmployeeError("❌ Employee ID already exists!");
        } else {
          setExistingEmployeeError("");
        }
      }
    } catch (error) {
      console.error("❌ Error checking Employee ID:", error);
    }
  };

  // ✅ Check if RFID Badge Number Already Exists
  const checkIfRfidExists = async (rfidBadgeNumber) => {
    try {
      if (rfidBadgeNumber.length === 4) {
        const res = await axios.get(`http://localhost:5000/api/employees/check-rfid/${rfidBadgeNumber}`);
        if (res.data.exists) {
          setRfidError("❌ RFID Badge Number already exists!");
        } else {
          setRfidError("");
        }
      }
    } catch (error) {
      console.error("❌ Error checking RFID Badge Number:", error);
    }
  };

  // ✅ Handle file input changes (Profile Picture)
  const handleFileChange = (e) => {
    const newFile = e.target.files[0];
    if (newFile) {
      const validTypes = ["image/jpeg", "image/png", "image/gif"];
      const maxSize = 200 * 1024; // 200KB max file size

      if (!validTypes.includes(newFile.type)) {
        showMessage(true, "error", "Only image files are allowed (JPEG, PNG, GIF).");
        return;
      }
      if (newFile.size > maxSize) {
        showMessage(true, "error", "File size must be under 200KB.");
        return;
      }

      const confirmWhiteBg = window.confirm("Is the image background white?");
      if (!confirmWhiteBg) {
        showMessage(true, "error", "Please upload an image with a white background.");
        return;
      }

      setFile(newFile);
      setPreviewURL(URL.createObjectURL(newFile));
    }
  };

  // ✅ Clean up preview URL to prevent memory leaks
  useEffect(() => {
    return () => {
      if (previewURL) {
        URL.revokeObjectURL(previewURL);
      }
    };
  }, [previewURL]);

  // ✅ Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prevent submission if Employee ID or RFID Badge Number is invalid or exists
    if (employeeIdError || rfidError || existingEmployeeError) {
      showMessage(true, "error", "❌ Please fix the errors before submitting.");
      return;
    }

    if (!employee.employeeId || !employee.firstName || !employee.lastName || !employee.department) {
      showMessage(true, "error", "⚠️ Please fill in all required fields.");
      return;
    }

    const employeeData = new FormData();
    Object.entries(employee).forEach(([key, value]) => {
      employeeData.append(key, value);
    });

    if (file) {
      employeeData.append("file", file);
    }

    try {
      const response = await axios.post("http://localhost:5000/api/employees", employeeData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      showMessage(true, "success", "✅ Successfully added employee!");
      console.log("✅ Server Response:", response.data);

      // Reset form fields after success
      setEmployee({
        employeeId: "",
        firstName: "",
        lastName: "",
        department: "",
        rfidBadgeNumber: "",
      });
      setFile(null);
      setPreviewURL(null);
    } catch (error) {
      console.error("❌ Error adding employee:", error);
      const errorMsg = error.response?.data?.message || "❌ Failed to add employee. Please try again.";
      showMessage(true, "error", errorMsg);
    }
  };

  return (
    <>
      <Header />
      <div className="header">
        <h1>Add Employee</h1>
      </div>
      <section className="managePage">
        <form className="editForm" onSubmit={handleSubmit}>
          <div className="fields">
            <div className="imgColumn">
              <img src={previewURL || "http://localhost:5000/images/defaultPic.png"} alt="Profile Pic" />
              <label htmlFor="fileInput" className="fileUploadLabel">
                <i className="fa-solid fa-circle-plus addProfileIcon"></i>
                <span> Add Profile Pic</span>
              </label>
              <input type="file" id="fileInput" onChange={handleFileChange} style={{ display: "none" }} />
            </div>
            <div className="fieldsColumn">
              <div className="fieldRow">
                <label className="fieldLabel">Employee ID</label>
                <input type="text" name="employeeId" value={employee.employeeId} onChange={updateEmployee} className="addInputs" />
                {employeeIdError && <p className="error-msg">{employeeIdError}</p>}
                {existingEmployeeError && <p className="error-msg">{existingEmployeeError}</p>}
              </div>

              <div className="fieldRow">
                <label className="fieldLabel">First Name</label>
                <input type="text" name="firstName" value={employee.firstName} onChange={updateEmployee} className="addInputs" />
              </div>

              <div className="fieldRow">
                <label className="fieldLabel">Last Name</label>
                <input type="text" name="lastName" value={employee.lastName} onChange={updateEmployee} className="addInputs" />
              </div>

              <div className="fieldRow">
                <label className="fieldLabel">Department</label>
                <select name="department" value={employee.department} onChange={updateEmployee} className="addInputs">
                  <option value="">Select Department</option>
                  {["Computer Science", "Electrical", "Mechanical", "Civil", "Electronics"].map((dept) => (
                    <option key={dept} value={dept}>{dept}</option>
                  ))}
                </select>
              </div>

              <div className="fieldRow">
                <label className="fieldLabel">RFID Badge Number</label>
                <input type="text" name="rfidBadgeNumber" value={employee.rfidBadgeNumber} onChange={updateEmployee} className="addInputs" />
                {rfidError && <p className="error-msg">{rfidError}</p>}
              </div>
            </div>
          </div>

          <div className="btnContainer">
            <button type="submit" className="bottomButton">Add</button>
            <button type="button" className="bottomButton" onClick={() => navigate("/")}>Back</button>
          </div>
          {message.show && <Message {...message} removeMessage={showMessage} />}
        </form>
      </section>
    </>
  );
}
