import React, { useState, useEffect } from "react";
import axios from "axios";
import io from "socket.io-client";
import { PieChart, Pie, Cell, Tooltip, Legend, LineChart, Line, XAxis, YAxis, CartesianGrid, ResponsiveContainer } from "recharts";
import Header from "../../components/header/Header";
import Pagination from "../../components/pagination/Pagination";
import "./Home.css";

export default function Home() {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [recordsPerPage] = useState(12);
  const [rfidBadgeNumber, setRfidBadgeNumber] = useState("");
  const [departmentStats, setDepartmentStats] = useState([]);
  const [roomStats, setRoomStats] = useState({
    canteen: 0,
    room1: 0,
    room2: 0,
    room3: 0,
    room4: 0,
    room5: 0,
  });

  // Fetch employees once on mount
  useEffect(() => {
    getEmployees();
  }, []);

  // Socket.io integration for real-time updates
  useEffect(() => {
    const socket = io("http://localhost:5000"); // Replace with your backend URL if different

    // Listen for updates (backend should emit "employee-update" events)
    socket.on("employee-update", (updatedEmployee) => {
      setEmployees((prevEmployees) => {
        const updatedEmployees = prevEmployees.map((emp) =>
          emp._id === updatedEmployee._id ? updatedEmployee : emp
        );
        // Update stats based on the new employees array
        updateDepartmentStats(updatedEmployees);
        updateRoomStats(updatedEmployees);
        return updatedEmployees;
      });
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  // ✅ Fetch Employees from Database
  const getEmployees = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/employees");
      setEmployees(res.data);
      updateDepartmentStats(res.data);
      updateRoomStats(res.data);
      setLoading(false);
    } catch (error) {
      console.error("❌ Error fetching employees:", error);
      setLoading(false);
    }
  };

  // ✅ Update Department Stats for Line Graph
  const updateDepartmentStats = (data) => {
    const today = new Date().toISOString().split("T")[0]; // Get today's date
    const departmentCounts = {};

    data.forEach((emp) => {
      if (emp.status === "loggedIn" && emp.loginTime?.startsWith(today)) {
        departmentCounts[emp.department] = (departmentCounts[emp.department] || 0) + 1;
      }
    });

    const formattedData = Object.entries(departmentCounts).map(([department, count]) => ({
      department,
      employees: count,
    }));

    setDepartmentStats(formattedData);
  };

  // ✅ Update Room Stats
  const updateRoomStats = (data) => {
    const roomCounts = {
      canteen: 0,
      room1: 48,
      room2: 20,
      room3: 10,
      room4: 0,
      room5: 0,
    };

    data.forEach((emp) => {
      if (emp.room) {
        roomCounts[emp.room] = (roomCounts[emp.room] || 0) + 1;
      }
    });

    setRoomStats(roomCounts);
  };

  // Gradient color based on room occupancy
  const getRoomGradientColor = (count) => {
    if (count <= 5) return "#0CCA4A"; // Dark green
    if (count <= 10) return "#66BB6A"; // Light green
    if (count <= 15) return "#FFEB3B"; // Yellow
    if (count <= 20) return "#FFA726"; // Orange
    if (count <= 25) return "#FF7043"; // Light red
    if (count <= 30) return "#F44336"; // Red
    if (count <= 35) return "#D32F2F"; // Dark red
    if (count <= 40) return "#B71C1C"; // Darker red
    if (count <= 45) return "#8E0000"; // Very dark red
    return "#5D0000"; // Threshold exceeded
  };

  // ✅ Handle RFID Login & Logout
  const handleRfidAction = async (action) => {
    if (!rfidBadgeNumber.trim()) {
      alert("⚠️ Please enter a valid RFID Number!");
      return;
    }

    setLoading(true);

    try {
      const res = await axios.post(`http://localhost:5000/api/employees/rfid-${action}`, { rfidBadgeNumber });
      alert(`✅ Employee ${action === "login" ? "Logged In" : "Logged Out"}: ${res.data.employee.firstName} ${res.data.employee.lastName}`);

      // Update employees state after login/logout
      const updatedEmployees = employees.map(emp =>
        emp.rfidBadgeNumber === rfidBadgeNumber ? res.data.employee : emp
      );
      setEmployees(updatedEmployees);
      updateDepartmentStats(updatedEmployees);
      updateRoomStats(updatedEmployees);

      setRfidBadgeNumber("");
    } catch (error) {
      console.error(`❌ Error scanning RFID for ${action}:`, error);
      alert(error.response?.data?.error || "⚠️ An unexpected error occurred.");
    } finally {
      setLoading(false);
    }
  };

  // ✅ Pagination Logic
  const indexOfLastRecord = currentPage * recordsPerPage;
  const indexOfFirstRecord = indexOfLastRecord - recordsPerPage;
  const currentRecords = employees.slice(indexOfFirstRecord, indexOfLastRecord);
  const nPages = Math.ceil(employees.length / recordsPerPage);

  // ✅ Pie Chart Data (Real-Time Login & Logout)
  const pieData = [
    { name: "Logged In", value: employees.filter(emp => emp.status === "loggedIn").length, color: "#0CCA4A" },
    { name: "Logged Out", value: employees.filter(emp => emp.status === "loggedOut").length, color: "#FFC107" },
  ];

  // ✅ Format Date and Time to 12-hour format
  const formatDateTime = (dateTime) => {
    if (!dateTime) return "N/A";
    const date = new Date(dateTime);
    const options = { hour: 'numeric', minute: 'numeric', hour12: true };
    return `${date.toLocaleDateString()} ${date.toLocaleTimeString([], options)}`;
  };

  // ✅ Get Status Color Dot
  const getStatusDot = (status) => (
    <span
      style={{
        height: "10px",
        width: "10px",
        backgroundColor: status === "loggedIn" ? "green" : "red",
        borderRadius: "50%",
        display: "inline-block",
        marginRight: "5px",
      }}
    ></span>
  );

  // Room status color and message
  const getRoomStatusColor = (count) => {
    if (count <= 40) return "#efef";  
    if (count <= 47) return "#efef";
    return "#efefef";
  };

  const getRoomStatusMessage = (count) => {
    if (count > 47) return "Room threshold crossed!";
    if (count > 40) return "Room nearing threshold!";
    return "";
  };

  return (
    <section className="main">
      {/* Loading Screen */}
      {loading && <div className="loading-screen">⏳ Loading Data...</div>}

      {/* Header */}
      <Header />

      {/* Room Sections */}
      <div className="room-container">
        {Object.entries(roomStats).map(([room, count]) => (
          <div
            key={room}
            className="room-section"
            style={{ backgroundColor: getRoomGradientColor(count) }}
          >
            <h3>{room.charAt(0).toUpperCase() + room.slice(1)}</h3>
            <p>{count} people</p>
            {getRoomStatusMessage(count) && (
              <p className="room-status-message">{getRoomStatusMessage(count)}</p>
            )}
            <ResponsiveContainer width="100%" height={100}>
              <PieChart>
                <Pie
                  data={[{ name: room, value: count }]}
                  cx="50%"
                  cy="50%"
                  outerRadius={40}
                  fill="#8884d8"
                  dataKey="value"
                >
                  <Cell fill={getRoomStatusColor(count)} />
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          </div>
        ))}
      </div>

      {/* RFID Scanner Section */}
      <div className="rfid-container">
        <h3>🔄 RFID Scanner</h3>
        <div className="rfid-input-group">
          <input
            type="text"
            value={rfidBadgeNumber}
            onChange={(e) => setRfidBadgeNumber(e.target.value)}
            placeholder="Enter RFID Number"
            className="rfid-input"
          />
          <div className="rfid-buttons">
            <button className="rfid-btn login-btn" onClick={() => handleRfidAction("login")}>🟢 Log In</button>
            <button className="rfid-btn logout-btn" onClick={() => handleRfidAction("logout")}>🔴 Log Out</button>
          </div>
        </div>
      </div>

      {/* Pie Chart & Line Chart */}
      <div className="chart-container">
        <div className="chart-wrapper">
          <h2>📊 Employee Login Status</h2>
          <PieChart width={400} height={300}>
            <Pie data={pieData} cx="50%" cy="50%" outerRadius={100} fill="#8884d8" dataKey="value" label>
              {pieData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={entry.color} />
              ))}
            </Pie>
            <Tooltip />
            <Legend />
          </PieChart>
        </div>

        <div className="chart-wrapper">
          <h2>📈 Daily Department Attendance</h2>
          {departmentStats.length > 0 ? (
            <LineChart width={400} height={300} data={departmentStats}>
              <XAxis dataKey="department" />
              <YAxis label={{ value: "No. of People", angle: -90, position: "insideLeft" }} />
              <CartesianGrid stroke="#ccc" />
              <Tooltip />
              <Line type="monotone" dataKey="employees" stroke="#0CCA4A" strokeWidth={2} />
            </LineChart>
          ) : (
            <p>No login data available for today.</p>
          )}
        </div>
      </div>

      {/* Updated Employee Table (Do Not Remove) */}
      <div className="table-container">
        <div className="table-header">
          <h2>📋 Employee Records</h2>
          <div className="table-buttons">
            <button onClick={() => window.location.href = "/employee/add"} className="top-btn">➕ Add Employee</button>
          </div>
        </div>
        <table className="employee-table">
          <thead>
            <tr>
              <th>Employee ID</th>
              <th>Name</th>
              <th>Status</th>
              <th>Timestamp</th>
            </tr>
          </thead>
          <tbody>
            {currentRecords.map((emp) => (
              <tr key={emp._id}>
                <td>{emp.employeeId}</td>
                <td>{emp.firstName} {emp.lastName}</td>
                <td>{getStatusDot(emp.status)}{emp.status}</td>
                <td>{formatDateTime(emp.loginTime)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Pagination */}
      <Pagination nPages={nPages} currentPage={currentPage} setCurrentPage={setCurrentPage} />
    </section>
  );
}
