import axios from "axios";
import React, { useState, useEffect, useCallback } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "./delete.css";
import Message from "../../components/message/Message";
import Header from "../../components/header/Header";

export default function Delete() {
  const navigate = useNavigate();
  const { id } = useParams();

  const [employee, setEmployee] = useState({
    employeeId: "",
    firstName: "",
    lastName: "",
    department: "",
    rfidBadgeNumber: "",
    imagePic: "",
  });

  const [message, setMessage] = useState({
    show: false,
    msg: "",
    type: "",
  });

  // ✅ Function to show or hide messages
  const showMessage = useCallback((show = false, type = "", msg = "") => {
    setMessage({ show, type, msg });
    if (show) {
      setTimeout(() => setMessage({ show: false, msg: "", type: "" }), 3000);
    }
  }, [showMessage]);

  // ✅ Fetch employee data
  const getEmployee = useCallback(async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/employees/${id}`);
      setEmployee(res.data);
    } catch (error) {
      showMessage(true, "error", "Failed to fetch employee data.");
    }
  }, [id]);

  useEffect(() => {
    getEmployee();
  }, [getEmployee]);

  // ✅ Handle employee deletion
  const handleDelete = async (e) => {
    e.preventDefault();
    try {
      await axios.delete(`http://localhost:5000/api/employees/${id}`);
      showMessage(true, "success", "Successfully deleted employee.");
      setTimeout(() => navigate("/"), 2000);
    } catch (error) {
      showMessage(true, "error", "Failed to delete employee.");
    }
  };

  return (
    <>
      <Header />
      <div className="header">
        <h1>Delete Employee</h1>
      </div>
      <section className="managePage">
        <form className="editForm" onSubmit={handleDelete}>
          <div className="fields">
            <div className="imgColumn">
              <img
                src={employee.imagePic ? `http://localhost:5000/${employee.imagePic}` : "http://localhost:5000/images/defaultPic.png"}
                alt="Profile Pic"
              />
            </div>
            <div className="fieldsColumn">
              {Object.entries(employee).map(([key, value]) =>
                key !== "imagePic" ? (
                  <div className="fieldRow" key={key}>
                    <label htmlFor={key} className="fieldLabel">{key.replace(/([A-Z])/g, " $1")}</label>
                    <input type="text" id={key} value={value} readOnly className="deleteInputs" />
                  </div>
                ) : null
              )}
            </div>
          </div>
          <div className="btnContainer">
            <button type="submit" className="bottomButton">Delete</button>
            <button type="button" className="bottomButton" onClick={() => navigate("/")}>Back</button>
          </div>
          {message.show && <Message {...message} removeMessage={showMessage} />}
        </form>
      </section>
    </>
  );
}
