import axios from "axios";
import React, { useState, useCallback, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../../components/header/Header";
import Message from "../../components/message/Message";
import "./add.css";

// Enable CORS
axios.defaults.headers.common['Access-Control-Allow-Origin'] = '*';

export default function Add() {
  const navigate = useNavigate();

  const [employee, setEmployee] = useState({
    employeeId: "",
    firstName: "",
    lastName: "",
    department: "",
    rfidBadgeNumber: "",
  });

  const [file, setFile] = useState(null);
  const [previewURL, setPreviewURL] = useState(null);
  const [message, setMessage] = useState({
    show: false,
    msg: "",
    type: "",
  });

  // Function to show messages with auto-dismiss
  const showMessage = useCallback((show = false, type = "", msg = "") => {
    setMessage({ show, type, msg });
    if (show) {
      setTimeout(() => setMessage({ show: false, msg: "", type: "" }), 3000);
    }
  }, []);

  // Handle input field changes
  const updateEmployee = (e) => {
    const { name, value } = e.target;
    setEmployee((prev) => ({ ...prev, [name]: value }));
  };

  // Handle file input changes
  const handleFileChange = (e) => {
    const newFile = e.target.files[0];
    setFile(newFile);

    // Create a preview URL for the selected file
    if (newFile) {
      setPreviewURL(URL.createObjectURL(newFile));
    }
  };

  // Clean up preview URL to prevent memory leaks
  useEffect(() => {
    return () => {
      if (previewURL) {
        URL.revokeObjectURL(previewURL);
      }
    };
  }, [previewURL]);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate that required fields are filled
    if (!employee.employeeId || !employee.firstName || !employee.lastName || !employee.department) {
      showMessage(true, "error", "Please fill in all required fields.");
      return;
    }

    const employeeData = new FormData();
    Object.entries(employee).forEach(([key, value]) => {
      employeeData.append(key, value);
    });

    if (file) {
      employeeData.append("file", file);
    }

    try {
      const response = await axios.post("http://localhost:5000/api/employees", employeeData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      showMessage(true, "success", "Successfully added employee information.");
      console.log("Response from server:", response.data);

      // Redirect after a short delay
      setTimeout(() => navigate("/"), 2000);
    } catch (error) {
      console.error("Error adding employee:", error);

      // Log detailed error information
      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        console.error("Response data:", error.response.data);
        console.error("Response status:", error.response.status);
        console.error("Response headers:", error.response.headers);
      } else if (error.request) {
        // The request was made but no response was received
        console.error("Request data:", error.request);
      } else {
        // Something happened in setting up the request that triggered an Error
        console.error("Error message:", error.message);
      }

      // Extract a meaningful error message
      const errorMsg = error.response?.data?.message || "Failed to add the employee. Please try again.";
      showMessage(true, "error", errorMsg);
    }
  };

  return (
    <>
      <Header />
      <div className="header">
        <h1>Add Employee</h1>
      </div>
      <section className="managePage">
        <form className="editForm" onSubmit={handleSubmit}>
          <div className="fields">
            <div className="imgColumn">
              <img
                src={previewURL || "http://localhost:5000/images/defaultProfilePic.png"}
                alt="Profile Pic"
              />
              <label htmlFor="fileInput" className="fileUploadLabel">
                <i className="fa-solid fa-circle-plus addProfileIcon"></i>
                <span> Add Profile Pic</span>
              </label>
              <input
                type="file"
                id="fileInput"
                onChange={handleFileChange}
                style={{ display: "none" }}
              />
            </div>
            <div className="fieldsColumn">
              {Object.entries(employee).map(([key, value]) => (
                <div className="fieldRow" key={key}>
                  <label htmlFor={key} className="fieldLabel">
                    {key.replace(/([A-Z])/g, " $1").replace(/^./, str => str.toUpperCase())}
                  </label>
                  <input
                    type="text"
                    name={key}
                    id={key}
                    value={value}
                    onChange={updateEmployee}
                    className="addInputs"
                  />
                </div>
              ))}
            </div>
          </div>

          <div className="btnContainer">
            <button type="submit" className="bottomButton">
              Add
            </button>
            <button type="button" className="bottomButton" onClick={() => navigate("/")}>
              Back
            </button>
          </div>
          
          {message.show && <Message {...message} removeMessage={showMessage} />}
        </form>
      </section>
    </>
  );
}
