import axios from "axios";
import React, { useState, useEffect, useCallback } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "./edit.css";
import Message from "../../components/message/Message";
import Header from "../../components/header/Header";

export default function Edit() {
  const navigate = useNavigate();
  const { id } = useParams();

  const [employee, setEmployee] = useState({
    employeeId: "",
    firstName: "",
    lastName: "",
    department: "",
    rfidBadgeNumber: "",
    imagePic: "",
  });

  const [file, setFile] = useState(null);
  const [previewURL, setPreviewURL] = useState(null);
  const [message, setMessage] = useState({ show: false, msg: "", type: "" });

  // ✅ Function to show messages with auto-dismiss
  const showMessage = useCallback((show = false, type = "", msg = "") => {
    setMessage({ show, type, msg });
    if (show) {
      setTimeout(() => setMessage({ show: false, msg: "", type: "" }), 3000);
    }
  }, []);

  // ✅ Fetch employee data
  const getEmployee = useCallback(async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/employees/${id}`);
      setEmployee(res.data);
    } catch (error) {
      showMessage(true, "error", "Failed to fetch employee data.");
    }
  }, [id]);

  useEffect(() => {
    getEmployee();
  }, [getEmployee]);

  // ✅ Handle input field changes
  const updateEmployee = (e) => {
    const { name, value } = e.target;
    setEmployee((prev) => ({ ...prev, [name]: value }));
  };

  // ✅ Handle file input changes (Profile Picture)
  const handleFileChange = (e) => {
    const newFile = e.target.files[0];
    if (newFile) {
      const validTypes = ["image/jpeg", "image/png", "image/gif"];
      const maxSize = 200 * 1024; // 200KB max file size

      if (!validTypes.includes(newFile.type)) {
        showMessage(true, "error", "Only image files are allowed (JPEG, PNG, GIF).");
        return;
      }
      if (newFile.size > maxSize) {
        showMessage(true, "error", "File size must be under 200KB.");
        return;
      }

      setFile(newFile);
      setPreviewURL(URL.createObjectURL(newFile));
    }
  };

  // ✅ Handle form submission (Update Employee)
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!employee.employeeId || !employee.firstName || !employee.lastName || !employee.department) {
      showMessage(true, "error", "⚠️ Please fill in all required fields.");
      return;
    }

    const employeeData = new FormData();
    Object.entries(employee).forEach(([key, value]) => {
      employeeData.append(key, value);
    });

    if (file) {
      employeeData.append("file", file);
    }

    try {
      await axios.put(`http://localhost:5000/api/employees/${id}`, employeeData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      showMessage(true, "success", "✅ Successfully updated employee!");
      setTimeout(() => navigate("/"), 2000);
    } catch (error) {
      console.error("❌ Error updating employee:", error);
      showMessage(true, "error", "❌ Failed to update employee.");
    }
  };

  return (
    <>
      <Header />
      <div className="header">
        <h1>Edit Employee</h1>
      </div>
      <section className="managePage">
        <form className="editForm" onSubmit={handleSubmit}>
          <div className="fields">
            <div className="imgColumn">
              <img
                src={
                  previewURL || 
                  (employee.imagePic ? `http://localhost:5000/${employee.imagePic}` : "http://localhost:5000/images/defaultPic.png")
                }
                alt="Profile Pic"
              />
              <label htmlFor="fileInput" className="fileUploadLabel">
                <i className="fa-solid fa-circle-plus addProfileIcon"></i> Update Profile Pic
              </label>
              <input type="file" id="fileInput" onChange={handleFileChange} style={{ display: "none" }} />
            </div>
            <div className="fieldsColumn">
              {Object.entries(employee).map(([key, value]) =>
                key !== "imagePic" ? (
                  <div className="fieldRow" key={key}>
                    <label htmlFor={key} className="fieldLabel">{key.replace(/([A-Z])/g, " $1")}</label>
                    <input type="text" name={key} id={key} value={value} onChange={updateEmployee} className="editInputs" />
                  </div>
                ) : null
              )}
            </div>
          </div>
          <div className="btnContainer">
            <button type="submit" className="bottomButton">Save Changes</button>
            <button type="button" className="bottomButton" onClick={() => navigate("/")}>Cancel</button>
          </div>
          {message.show && <Message {...message} removeMessage={showMessage} />}
        </form>
      </section>
    </>
  );
}
