import React, { useState, useRef } from 'react';
import axios from 'axios';
import './app.css';
import TiltedCard from './TiltedCard';
import nakulImage from './assets/Nakul.jpg'; // Adjust path if necessary

function App() {
  const [file, setFile] = useState(null);
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);
  const fileInputRef = useRef(null);

  const onFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSelectFile = () => {
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  const onFileUpload = async () => {
    if (!file) {
      alert('Please select a file first!');
      return;
    }
    setLoading(true);

    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('http://localhost:5000/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setResponse(res.data.clean_text);
    } catch (error) {
      console.error('Error uploading file:', error);
      setResponse('Error uploading file');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      {/* Landing Section */}
      <div className="landing-section">
        <h1 className="main-heading">Tender Summarizer</h1>
        <p className="subheading">
          Summarize your tender documents quickly and easily in one place.
        </p>

        <button className="primary-button" onClick={handleSelectFile}>
          <i className="fas fa-file-upload"></i> Select PDF files
        </button>

        <input
          type="file"
          ref={fileInputRef}
          style={{ display: 'none' }}
          onChange={onFileChange}
          accept="application/pdf"
        />

        {file && <p className="selected-file">Selected file: {file.name}</p>}

        <button className="upload-button" onClick={onFileUpload} disabled={loading}>
          {loading ? 'Uploading...' : 'Upload'}
        </button>
      </div>

      {response && (
        <div className="response-section">
          <h2>Summary Response</h2>
          <div className="response-card">
            {loading ? <div className="loader"></div> : <p>{response}</p>}
          </div>
        </div>
      )}
      <div className="team-cards">
        {/* Team Member 1 */}
        <TiltedCard
          imageSrc={nakulImage}
          altText="Nakul Chandak"
          captionText="Jr Devloper"
          containerHeight="300px"
          containerWidth="200px"
          imageHeight="300px"
          imageWidth="200px"
          rotateAmplitude={12}
          scaleOnHover={1.2}
          showMobileWarning={false}
          showTooltip={true}
          displayOverlayContent={true}
          overlayContent={
            <div>
              <p style={{ color: 'black' }}>Nakul Chandak</p>
              <a href="https://www.linkedin.com/in/nakul-chandak-7280151b8" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-linkedin" style={{ fontSize: '2rem' }}></i>
              </a>
              <br />
              <a href="https://github.com/nakulchandak89" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-github" style={{ fontSize: '2rem', color: 'black' }}></i>
              </a>
            </div>
          }
        />

        {/* Team Member 2 */}
        <TiltedCard
          imageSrc="path/to/photo2.jpg"
          altText="Shriram Dixit"
          captionText="Sr devloper"
          containerHeight="300px"
          containerWidth="200px"
          imageHeight="300px"
          imageWidth="200px"
          rotateAmplitude={12}
          scaleOnHover={1.2}
          showMobileWarning={false}
          showTooltip={true}
          displayOverlayContent={true}
          overlayContent={
            <div>
              <p>Shriram Dixit</p>
              <a href="https://www.linkedin.com/in/member2" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-linkedin" style={{ fontSize: '2rem' }}></i>
              </a>
              <br />
              <a href="https://github.com/member2" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-github" style={{ fontSize: '2rem', color: 'black' }}></i>
              </a>
            </div>
          }
        />

        {/* Team Member 3 */}
        <TiltedCard
          imageSrc="path/to/photo3.jpg"
          altText="Vedant Pandhare"
          captionText="Project Manager"
          containerHeight="300px"
          containerWidth="200px"
          imageHeight="300px"
          imageWidth="200px"
          rotateAmplitude={12}
          scaleOnHover={1.2}
          showMobileWarning={false}
          showTooltip={true}
          displayOverlayContent={true}
          overlayContent={
            <div>
              <p>Vedant Pandhare</p>
              <a href="https://www.linkedin.com/in/member3" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-linkedin" style={{ fontSize: '2rem' }}></i>
              </a>
              <br />
              <a href="https://github.com/member3" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-github" style={{ fontSize: '2rem', color: 'black' }}></i>
              </a>
            </div>
          }
        />

        {/* Team Member 4 */}
        <TiltedCard
          imageSrc="path/to/photo4.jpg"
          altText="Shauriya Ahire"
          captionText="data analytics"
          containerHeight="300px"
          containerWidth="200px"
          imageHeight="300px"
          imageWidth="200px"
          rotateAmplitude={12}
          scaleOnHover={1.2}
          showMobileWarning={false}
          showTooltip={true}
          displayOverlayContent={true}
          overlayContent={
            <div>
              <p>Shauriya Ahire</p>
              <a href="https://www.linkedin.com/in/member4" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-linkedin" style={{ fontSize: '2rem' }}></i>
              </a>
            </div>
          }
        />
      </div>
    </div>
  );
}

export default App;
